/**
 * Responsive Video
 * A JavaScript file for making all video embeds responsive.
 *
 */
 
(function ($, Drupal, window, document, undefined) {

Drupal.behaviors.responsiveVideo =  {
  attach: function (context, settings) {
    // An array for different video choices.
    var elements = ['.bonnier_video_playlist','iframe[src^="//www.youtube.com/embed/"]', 'iframe[src^="http://www.youtube.com/embed/"]', 'iframe[src^="//player.vimeo.com/video/"]', 'iframe[src^="http://player.vimeo.com/video/"]'];
    // Remove heights/widths from media_embed before adjusting.
    $('.media_embed').css('height','auto').css('width','auto');
    $.each(elements, function(i, video) {     
      // Attach the aspect ratio to each element
      $(video).each(function() {
        var width = $(this).width();
        var height = $(this).height();
        $(this).data('aspectRatio', width / height);
        $(this).addClass('responsive-video-player');
      });
    });
    // Resize video anytime the window is resized.
    $(window).resize(function() {
      $('.responsive-video-player').each(function() {
        $(this).css('width', 'auto');
        var newWidth = $(this).parent().width();
        var newHeight = Math.round(newWidth / $(this).data('aspectRatio'));
        $(this).css('width', newWidth).css('height',newHeight);
      });
    }).resize();
  }
}  

})(jQuery, Drupal, this, this.document);
  
  
  
  
