(function ($, Drupal, window, document, undefined) {

// Establish Bonnier as a Drupal object.
Drupal.Bonnier = Drupal.Bonnier || {};

// Gallery Sliders
Drupal.Bonnier.GallerySlider = {
  // Load the gallery images
  'loadImages': function(slider, slideNum) {
    if (slideNum == undefined) return;
    // Create the slide numbers array
    var slideNums = [];
    // remove the loaded class on images
    $('img.loaded', slider).removeClass('loaded');
    // Check to see if it's a carousel and change the sliding type.  
    if (slider.vars.itemWidth > 0) {
			var count = slider.visible;
      var start = (slideNum * count) - (count * 2);
      var end = (slideNum * count) + (count * 4); 
      for(i=start; i < end; i++) {
        slideNums.push(i);
      }
    } else {
      // load in new images depending on the slide number.
      switch(slideNum) {
        case 0 :
        slideNums = [slideNum, slideNum + 1, slider.count - 1];
        break;
        case 1 :
        slideNums = [slideNum, slideNum + 1, slideNum - 1];
        break;
        case slider.count - 1 :
        slideNums = [slideNum, slideNum - 1, 0];
        break;
        default :
        slideNums = [slideNum, slideNum + 1, slideNum - 1];
      }
    }
    // Loop through the slideNums and change the src attribute.
    for (i = 0; i < slideNums.length; i++) { 
      var image = $('img', slider.slides[slideNums[i]]);
      image.each(function() {
        var source = $(this).attr("data-src");
        if (source) {
          // Add a class of loaded to be able to remove other images.
          $(this).attr("src", source).addClass('loaded');
        }
      });
    }
  },
  // Load both of the clones
  'loadClones': function(slider) {
    $('li.clone img', slider).each(function() {
      var source = $(this).attr("data-src");
      if (source) {
        $(this).attr("src", source).addClass('clone-img');
      }
    });
  },
  // Change the height of the gallery
  'changeHeight' : function(slider, slideNum) {
    var height = slider.slides[slideNum].clientHeight;
    if (slider.vars.animation == 'fade') {
      $('ul.slides', slider).css('height',height);
    } else {
      $('.flex-viewport', slider).css('height',height);
    }
  }, 
  // Get the url parameter to change slides
  'getURLParameter' : function(name) {
    return decodeURIComponent((new RegExp('[?|&]' + name + '=' + '([^&;]+?)(&|#|;|$)').exec(location.search)||[,""])[1].replace(/\+/g, '%20'))||null;
  },
  'getDirectionNav' : function(name, slider) {
    return Boolean($(slider).parents(name).attr('data-pager') == 1 ? true : false) || null;
  },
  // common gallery functions.
  'slideStart' : function(slider, heightChange) {
    var gs = this;
    // Load any cloned images.
    gs.loadClones(slider);
    // Load the next few images.
    gs.loadImages(slider, slider.currentSlide);
    if (heightChange == true) {
      // On completed load of the current image, update the height.
      $('img', slider.slides[slider.currentSlide]).load(function() {
        gs.changeHeight(slider,slider.currentSlide);
      });
      $(window).resize(function() {
        gs.changeHeight(slider,slider.currentSlide);
      });
    }
    // Add classes based on simple conditions
    if (slider.count < 2) {
      slider.addClass('single-image');
    }
    if (slider.vars.controlNav == true) {
      slider.addClass('controls');
    }
    // Add a class of the animation type for transition effects
    slider.addClass(slider.vars.animation);
  },
  'slideBefore' : function(slider, heightChange) {
    var gs = this;
    if (heightChange == true) {
      // Change the height of the slider to what image is coming up next.
      gs.changeHeight(slider,slider.animatingTo);
    }
    if (slider.vars.controlNav == true) {
      // Load upcoming images.
      gs.loadImages(slider, slider.animatingTo);
    }
  },
  'slideAfter' : function(slider, heightChange) {
    var gs = this;
    if (heightChange == true) {
      // In case of multiple clicks at once, on the last image load, change height.
      $('img', slider.slides[slider.currentSlide]).load(function() {
        gs.changeHeight(slider,slider.currentSlide);
      });
    }
    // Load upcoming images.
    gs.loadImages(slider, slider.currentSlide);
    // Set the rest of the images that are not loaded back to empty source.
    $('img', slider).not('.loaded, .clone-img').each(function() {
      $(this).attr('src',Drupal.settings.basePath + Drupal.settings.pathToTheme + '/images/placeholder.gif');
    });
  },
  'refresh' : function(slider) {
    // Set the history of the current gallery.
    History.replaceState({state:slider.currentSlide}, document.title, '?image=' + slider.currentSlide + '');
    // Refresh the ads.
    Drupal.attachBehaviors();
  },
} /* End Galleries */

// Lazy load images and unveil them, except for images inside Flexslider.
Drupal.behaviors.loadImages = {
  attach: function (context, settings) {    
    $('img').once('unveil').each(function() {
      if ($(this).parents('.slides').length > 0) {
        $(this).addClass('slide-image');
      } else {
        $(this).unveil(500, function() {
          if (this.complete || this.readyState === 4) {
            $(this).addClass('full-visible');
          } else {
            $(this).load(function() {
              $(this).addClass('full-visible');
            });
          }
        });
      }
    });
  }
}

})(jQuery, Drupal, this, this.document);;
